/* Check if page has enough space on the right and move task bar into the empty space if possible */
NRWgov.moveTasks = function() {
  var html_width = jQuery('html').width();
  var page_width = jQuery('html > body > .texture-overlay > #page').width();
  var side_width = (html_width - page_width) / 2;
  var tasks = jQuery('#tasks');

  if (side_width > 100) {
    tasks.addClass('floating-tasks');
    tasks.css('position', 'fixed');
    tasks.css('right', '70px');
    tasks.css('top', '10%');
  } else {
    tasks.removeClass('floating-tasks');
    tasks.css('position', '');
    tasks.css('right', '');
    tasks.css('top', '');
  }

  var task_links = tasks.find('li a').each(function() {
    var element = jQuery(this);
    element.attr('title', element.text());
  });
}


